mapper.select:
		select `id`,`uuid`,`CName`,`EName`,`price`,`number`,`quality`,`material`,`describe`,`type`,`series`,`characteristic`,`market`,`colour`,`others`,`created`,`modified`,`gtm_create`,`gtm_modified`,`status`,`imgUrl`,`brand`,`brand_uuid`,`series_uuid` from goods

			 and uuid = #{uuid} 
			 and type = #{type} 
			 and CName like CONCAT('%',#{cname},'%') 
			 and brand_uuid = #{brandUuid} 
			 and status = #{status} 
			 and series_uuid = #{seriesUuid} 




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from goods

  		   and id = #{id} 
  		   and uuid = #{uuid} 

	 		 and CName like CONCAT('%',#{cname},'%') 
  		   and EName = #{ename} 
  		   and price = #{price} 
  		   and number = #{number} 
  		   and quality = #{quality} 
  		   and material = #{material} 
  		   and describe = #{describe} 
  		   and type = #{type} 
  		   and series = #{series} 
  		   and characteristic = #{characteristic} 
  		   and market = #{market} 
  		   and colour = #{colour} 
  		   and others = #{others} 
  		   and created = #{created} 
  		   and modified = #{modified} 
  		   and gtm_create = #{gtmCreate} 
  		   and gtm_modified = #{gtmModified} 
  		   and status = #{status} 
		  and brand_uuid = #{brandUuid} 
	  and series_uuid = #{seriesUuid} 
	  and imgUrl = #{imgurl} 
	  and brand = #{brand}

mapper.update:
update goods
`CName` = #{cname}, `EName` = #{ename}, `price` = #{price}, `number` = #{number}, `quality` = #{quality}, `material` = #{material}, `describe` = #{describe}, `type` = #{type}, `series` = #{series}, `characteristic` = #{characteristic}, `market` = #{market}, `colour` = #{colour}, `others` = #{others}, `created` = #{created}, `modified` = #{modified}, `gtm_create` = #{gtmCreate}, `gtm_modified` = #{gtmModified}, `status` = #{status}, `imgUrl` = #{imgurl}, `brand` = #{brand}, brand_uuid = #{brandUuid} , series_uuid = #{seriesUuid}
where uuid = #{uuid}
